mapper.insert:
INSERT INTO txt(
	txtno, title,  mid, catno, mtxt 
) VALUES (
	getTxtno.nextval, #{title}, #{mid}, #{catno}, #{mtxt}
)

mapper.update:
UPDATE txt
SET isshow = 'N' 
WHERE txtno = #{txtno}
	OR  upno = #{upno}

UPDATE txt
SET	title = #{title},
	mtxt = #{mtxt},
	catno = #{catno}
WHERE txtno = #{txtno}

UPDATE txt
SET count = count+1
WHERE txtno = #{txtno}

