SELECT mid , mname , mtel , substr(memail,1,instr(memail,'@')-1) email , substr(memail , instr(memail,'@')+1) domain ,mno , issue, joindate , joindate jointime FROM member WHERE
grade = 'M' AND mno = #{mno}
grade ='A' AND mid = #{mid}

mapper.update:
UPDATE 
    member
SET
    mname = #{mname} , mtel = #{mtel} , memail = #{memail}
    ,  issue = #{issue} 
    , mpw= #{mpw} 
WHERE

    grade = 'M'
    AND mno = #{mno}


	grade ='A'
	AND mid = #{mid}
